import React, { useState } from 'react'
import "./Project.css"
import ProjectItem from './Project/ProjectItem'
import {db} from "../firebase/firebase"
import { useEffect } from 'react'
import { collection, getDocs } from "firebase/firestore";
import {FaArrowLeft,FaArrowRight} from 'react-icons/fa'

export const Projects = () => {
  const [item, setItem] = useState([])

  //Retrieve user data from the database 
  useEffect(() =>{
    const fetchProjects = async () => {
      await getDocs(collection(db, "projects"))
      .then(querySnapshot => {
        querySnapshot.docs
          .map(doc => 
            setItem(item => [...item, doc.data()])
          );
      });
    }

    fetchProjects();

    console.log(item)
  },[])

  return (
    <div id="projects" className='comp'>
      <h1>&#123;$projects&#125;</h1>
      <section className='slider'>
        <FaArrowLeft className='left-arrow' style={{color:'white'}}/>
        <FaArrowRight className='right-arrow' style={{color:'white'}}/>
        {item.map(i => <ProjectItem item = {i}/>)}
      </section>
    </div>
  )
}
